import React from "react";
import styled from "styled-components";
import MecaElem from "../../assets/meca.png";
import { useEffect } from "react";

const CoalWrapper = styled.section`
  position: relative;
  width: 100vw;
  min-height: 100vh;
  background-color: #d8d6c8;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaContainer = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaWrapper = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaElem2 = styled.img`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  width: ${(props) => props.width}px;
  display: ${(props) => (props.display ? "none" : "flex")};
  transform: scale(0.8);
`;
let mecaSize = [
  {
    width: "100",
    display: false,
  },
  {
    width: "200",
    display: false,
  },
  {
    width: "400",
    display: false,
  },
  {
    width: "600",
    display: false,
  },
  {
    width: "800",
    display: false,
  },
  {
    width: "1100",
    display: false,
  },
];

const Coal = () => {
  useEffect(() => {
    let index = 0;
    let mecas = mecaSize;
    setInterval(() => {
      if (mecaSize.length > index) {
        mecas[index].display = true;
      }
      mecaSize = mecas;
      index++;
      console.log(mecaSize);
    }, 4000);
  }, []);

  return (
    <CoalWrapper>
      <MecaContainer>
        <MecaWrapper>
          {mecaSize.map((m) => {
            return (
              <MecaElem2
                width={m.width}
                display={m.display}
                src={MecaElem}
                alt="meca"
              />
            );
          })}
        </MecaWrapper>
      </MecaContainer>
    </CoalWrapper>
  );
};

export default Coal;
