import React, { useEffect } from "react";
import styled from "styled-components";
import MecaSrc from "../../assets/meca.png";
import { Waypoint } from "react-waypoint";
import { useState } from "react";

const InfiniteScroll = styled.section`
  height: 300vh;
  z-index: -1000;
`;

const CoalWrapper = styled.section`
  position: fixed;
  width: 100vw;
  min-height: 100vh;
  background-color: #d8d6c8;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaContainer = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaWrapper = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaElem = styled.img`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  width: ${(props) => props.width}px;
  display: ${({ display }) => (display ? "flex" : "none")};
  transform: scale(0.8);
`;
let mecaSize = [
  {
    width: "100",
    display: false,
  },
  {
    width: "200",
    display: false,
  },
  {
    width: "400",
    display: false,
  },
  {
    width: "600",
    display: false,
  },
  {
    width: "800",
    display: false,
  },
  {
    width: "1100",
    display: false,
  },
];

const Coal = () => {
  const [mecas, setMecas] = useState([]);
  const [scrollPosition, setSrollPosition] = useState(0);

  const addAMeca = (position) => {
    let len = mecas.length;
    let mecaSave = mecas;
    mecaSave.push(mecaSize[len]);
    setMecas(mecaSave);
    setSrollPosition(position);
  };

  const [tempScrollPosition, setTempSrollPosition] = useState(0);
  const handleScroll = () => {
    const position = window.pageYOffset;
    // console.log(window.innerHeight);
    if (scrollPosition < window.innerHeight) setTempSrollPosition(position);
    if (scrollPosition - 1 < tempScrollPosition) addAMeca(position);
    console.log(scrollPosition, tempScrollPosition);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll, { passive: true });

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <InfiniteScroll>
      <CoalWrapper>
        <MecaContainer>
          <MecaWrapper>
            {/* {mecas.map((m) => {
              return (
                <MecaElem
                  width={m.width}
                  display={!m.display}
                  src={MecaSrc}
                  alt="meca"
                />
              );
            })} */}
          </MecaWrapper>
        </MecaContainer>
      </CoalWrapper>
    </InfiniteScroll>
  );
};

export default Coal;
