import React, { useEffect } from "react";
import styled from "styled-components";
import MecaSrc from "../../assets/meca.png";
import { useState } from "react";

const InfiniteScroll = styled.section`
  height: 300vh;
  z-index: -1000;
`;

const CoalWrapper = styled.section`
  position: fixed;
  width: 100vw;
  min-height: 100vh;
  background-color: #d8d6c8;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaContainer = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaWrapper = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaElem = styled.img`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  width: ${(props) => props.width}px;
  display: ${({ display }) => (display ? "flex" : "none")};
  transform: scale(0.8);
`;
let mecaSize = [
  {
    width: "100",
    display: false,
  },
  {
    width: "200",
    display: false,
  },
  {
    width: "400",
    display: false,
  },
  {
    width: "600",
    display: false,
  },
  {
    width: "800",
    display: false,
  },
  {
    width: "1100",
    display: false,
  },
];

const Coal = () => {
  const [mecas, setMecas] = useState(mecaSize);

  const addAMeca = (i) => {
    // let mecaSave = mecas;
    // if (mecaSize[i] && !mecaSave.includes(mecaSize[i])) {
    //   mecaSave.push(mecaSize[i]);
    // }
    // setMecas(mecaSave);
    // console.log(mecaSave, mecas);
    let test = [...mecas, (mecaSize[i].display = true)];
    console.log(test);
    setMecas(test);
  };

  let lastScroll = 0;

  const handleScroll = () => {
    const position = window.pageYOffset;
    // if (lastScroll < position) console.log("bottom");
    // if (lastScroll > position) console.log("up");
    let upDirrection = false;
    if (lastScroll < position) {
      upDirrection = true;
    }
    if (position > 100 && position < 120 && upDirrection) addAMeca(0);
    if (position > 300 && position < 320 && upDirrection) addAMeca(1);
    if (position > 400 && position < 420 && upDirrection) addAMeca(2);
    if (position > 500 && position < 520 && upDirrection) addAMeca(3);
    if (position > 600 && position < 620 && upDirrection) addAMeca(4);
    lastScroll = position;
    console.log(position);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll, { passive: true });

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  console.log(mecas);
  return (
    <InfiniteScroll>
      <CoalWrapper>
        <MecaContainer>
          <MecaWrapper>
            {mecas.map((m) => {
              return (
                <MecaElem
                  width={m && m.width}
                  display={m && m.display}
                  src={MecaSrc}
                  alt="meca"
                />
              );
            })}
          </MecaWrapper>
        </MecaContainer>
      </CoalWrapper>
    </InfiniteScroll>
  );
};

export default Coal;
