import React from "react";
import styled from "styled-components";
import MecaElem from "../../assets/meca.png";

const CoalWrapper = styled.section`
  position: relative;
  width: 100vw;
  min-height: 100vh;
  background-color: #d8d6c8;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaContainer = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaWrapper = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaElem2 = styled.img`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  width: ${(props) => props.width}px;
  /* display: ${(props) => (props.display ? "flex" : "none")}; */
`;
const mecaSize = ["100", "200", "400", "600", "800", "1100"];
const mecaAnim = () => {
  let arr = [
    // <MecaElem2 width={mecaSize[2]} src={MecaElem} />,
    // <MecaElem2
    //   onClick={() => (arr = null)}
    //   width={mecaSize[3]}
    //   src={MecaElem}
    // />,
  ];
  // setTimeout(() => {
  //   for (let i = 0; i < mecaSize.length; i++) {}
  // }, 400);
  let index = 0;
  setInterval(() => {
    if (mecaSize.length - 1 > index) {
      arr.push(<MecaElem2 width={mecaSize[index]} src={MecaElem} />);
    }
    index++;
    console.log(index);
  }, 400);
  return arr;
};

const Coal = () => {
  return (
    <CoalWrapper>
      <MecaContainer>
        <MecaWrapper>
          {/* {mecaSize.map((m) => {
            return <MecaElem2 width={m} display={} src={MecaElem} alt="meca" />;
          })} */}
          {mecaAnim()}
        </MecaWrapper>
      </MecaContainer>
    </CoalWrapper>
  );
};

export default Coal;
