import React from "react";
import styled from "styled-components";
import MecaElem from "../../assets/meca.png";

const CoalWrapper = styled.section`
  position: relative;
  width: 100vw;
  min-height: 100vh;
  background-color: #d8d6c8;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaContainer = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaWrapper = styled.div`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const MecaElem2 = styled.img`
  position: absolute;
  display: flex;
  justify-content: center;
  align-items: center;
  width: ${(props) => props.width}px;
`;
const mecaSize = ["100", "200", "400", "600", "800", "1100"];

const mecadisplayed = {};

const mecaAnim = (m) => {
  for (let i = 0; i < mecaSize; i++) {
    setTimeout(() => {
      if (m === mecaSize[i]) return true;
    }, 400);
  }
};

const Coal = () => {
  return (
    <CoalWrapper>
      <MecaContainer>
        <MecaWrapper>
          {mecaSize.map((m) => {
            return (
              <MecaElem2
                width={m}
                display={mecaAnim(m)}
                src={MecaElem}
                alt="meca"
              />
            );
          })}
        </MecaWrapper>
      </MecaContainer>
    </CoalWrapper>
  );
};

export default Coal;
